// Initialize the parallax effect
const card = this.querySelector('.parallax-card');
const layers = this.querySelectorAll('.parallax-layer');

function handleMouseMove(e) {
  const rect = card.getBoundingClientRect();
  const centerX = rect.left + rect.width / 2;
  const centerY = rect.top + rect.height / 2;
  const mouseX = e.clientX - centerX;
  const mouseY = e.clientY - centerY;

  layers.forEach(layer => {
    const speed = parseFloat(layer.getAttribute('data-speed')) || 0;
    const rotateY = mouseX * speed * 0.03;
    const rotateX = -mouseY * speed * 0.03;

    layer.style.transform = `translateZ(${speed * 100}px) rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;
  });
}

card.addEventListener('mousemove', handleMouseMove);
card.addEventListener('mouseleave', () => {
  layers.forEach(layer => {
    const speed = parseFloat(layer.getAttribute('data-speed')) || 0;
    layer.style.transform = `translateZ(${speed * 100}px)`;
  });
});

// Add subtle entrance animation
function animateIn() {
  setTimeout(() => {
    card.style.transform = 'translateY(0)';
    card.style.opacity = '1';

    layers.forEach((layer, index) => {
      setTimeout(() => {
        const speed = parseFloat(layer.getAttribute('data-speed')) || 0;
        layer.style.transform = `translateZ(${speed * 100}px)`;
        layer.style.opacity = '1';
      }, 100 * index);
    });
  }, 300);
}

// Setup initial state
card.style.transform = 'translateY(20px)';
card.style.opacity = '0';
card.style.transition = 'transform 0.6s ease-out, opacity 0.6s ease-out';

layers.forEach(layer => {
  layer.style.transform = 'translateZ(0)';
  layer.style.opacity = '0';
  layer.style.transition = 'transform 0.6s ease-out, opacity 0.6s ease-out';
});

// Start animation
animateIn();

