  background: rgba(120, 150, 255, 0.9);
  box-shadow: 0 10px 30px rgba(100, 120, 240, 0.5);
  z-index: 10;
}

.iso-cube:nth-child(odd) {
  background: rgba(80, 100, 220, 0.8);
  animation: float 4s infinite ease-in-out;
}

.iso-cube:nth-child(2n+1) {
  animation-delay: 0.5s;
}

.iso-cube:nth-child(3n+1) {
  animation-delay: 1s;
}

.iso-cube:nth-child(5) {
  background: rgba(120, 150, 255, 0.9);
  animation-delay: 1.5s;
}

