// Interactive Floating Elements
function initFloatingElements() {
  const container = document.querySelector('.floating-container');
  const elements = document.querySelectorAll('.floating-element');

  if (!container || !elements.length) return;

  // Initialize element positions with slight random movement
  elements.forEach(el => {
    const randomX = Math.random() * 10 - 5;
    const randomY = Math.random() * 10 - 5;

    // Store original positions
    const rect = el.getBoundingClientRect();
    const containerRect = container.getBoundingClientRect();

    el.dataset.originalX = (rect.left - containerRect.left) + randomX;
    el.dataset.originalY = (rect.top - containerRect.top) + randomY;

    // Start floating animation using CSS
    el.style.setProperty('--float-x', Math.random() * 20 - 10 + 'px');
    el.style.setProperty('--float-y', Math.random() * 20 - 10 + 'px');
    el.style.setProperty('--float-rotate', Math.random() * 20 - 10 + 'deg');
    el.style.setProperty('--float-duration', 3 + Math.random() * 4 + 's');
    el.classList.add('floating');
  });

  // Add mouse move interactivity
  container.addEventListener('mousemove', e => {
    const containerRect = container.getBoundingClientRect();
    const mouseX = e.clientX - containerRect.left;
    const mouseY = e.clientY - containerRect.top;

    elements.forEach(el => {
      // Calculate distance from mouse to element
      const elRect = el.getBoundingClientRect();
      const elCenterX = elRect.left - containerRect.left + elRect.width / 2;
      const elCenterY = elRect.top - containerRect.top + elRect.height / 2;

      const distanceX = mouseX - elCenterX;
      const distanceY = mouseY - elCenterY;
      const distance = Math.sqrt(distanceX * distanceX + distanceY * distanceY);

      // Create repulsion effect within a certain radius
      const radius = 150;

      if (distance < radius) {
        // Calculate force - closer means stronger force
        const force = (1 - distance / radius) * 25;

        // Calculate angle
        const angle = Math.atan2(distanceY, distanceX);

        // Calculate repulsion movement (opposite direction from mouse)
        const moveX = -Math.cos(angle) * force;
        const moveY = -Math.sin(angle) * force;

        // Apply movement with CSS transitions
        el.style.transform = 'translate(' + moveX + 'px, ' + moveY + 'px) rotate(' + (mouseX / containerRect.width - 0.5) * 15 + 'deg)';
        el.style.transition = 'transform 0.6s ease';
      } else {
        // Return to floating state if mouse is far away
        returnToFloating(el);
      }
    });
  });

  // Return elements to floating state when mouse leaves
  container.addEventListener('mouseleave', () => {
    elements.forEach(el => {
      returnToFloating(el);
    });
  });

  // Make elements clickable for an extra effect
  elements.forEach(el => {
    el.addEventListener('click', () => {
      // Create a fun "pop" effect
      el.classList.add('pop');
      setTimeout(() => {
        el.classList.remove('pop');
      }, 600);
    });
  });

  function returnToFloating(element) {
    // Reset to original floating animation
    element.classList.remove('floating');
    element.style.transform = '';

    // Re-trigger floating animation
    setTimeout(() => {
      element.style.setProperty('--float-x', Math.random() * 20 - 10 + 'px');
      element.style.setProperty('--float-y', Math.random() * 20 - 10 + 'px');
      element.style.setProperty('--float-rotate', Math.random() * 20 - 10 + 'deg');
      element.style.setProperty('--float-duration', 3 + Math.random() * 4 + 's');
      element.classList.add('floating');
    }, 10);
  }
}

// Initialize the floating elements
initFloatingElements();

