// Particle explosion animation
function createParticleExplosion() {
  const particlesArea = document.querySelector('.particles-area');
  const triggerBtn = document.getElementById('trigger-particles');

  if (!particlesArea || !triggerBtn) return;

  triggerBtn.addEventListener('click', () => {
    // Clear any existing particles
    particlesArea.innerHTML = '';

    // Get the button position for the explosion center
    const rect = triggerBtn.getBoundingClientRect();
    const containerRect = particlesArea.getBoundingClientRect();

    const centerX = rect.left - containerRect.left + rect.width / 2;
    const centerY = rect.top - containerRect.top + rect.height / 2;

    // Create particles
    const particleCount = 50;

    for (let i = 0; i < particleCount; i++) {
      const particle = document.createElement('div');
      particle.classList.add('particle');

      // Random size between 5 and 15px
      const size = 5 + Math.random() * 10;
      particle.style.width = size + 'px';
      particle.style.height = size + 'px';

      // Random color variations
      const hue = 260 + Math.random() * 40; // Purple variations
      const saturation = 70 + Math.random() * 30;
      const lightness = 50 + Math.random() * 20;
      particle.style.backgroundColor = 'hsl(' + hue + ', ' + saturation + '%, ' + lightness + '%)';

      // Position at center
      particle.style.left = centerX + 'px';
      particle.style.top = centerY + 'px';

      particlesArea.appendChild(particle);

      // Animate particle with CSS
      const angle = Math.random() * Math.PI * 2; // Random angle
      const distance = 50 + Math.random() * 150; // Random distance
      const duration = 0.8 + Math.random() * 1.2; // Random duration

      // Set animation properties and add class
      particle.style.setProperty('--end-x', Math.cos(angle) * distance + 'px');
      particle.style.setProperty('--end-y', Math.sin(angle) * distance + 'px');
      particle.style.setProperty('--duration', duration + 's');
      particle.classList.add('explode');

      // Remove particle after animation completes
      setTimeout(() => {
        particle.remove();
      }, duration * 1000);
    }
  });
}

createParticleExplosion();

