function initPremiumCube() {
  const cube = document.querySelector('.premium-cube');
  if (!cube) return;

  let isDragging = false;
  let previousMousePosition = { x: 0, y: 0 };
  let rotationX = 20;
  let rotationY = 0;
  let lastTap = 0;
  let isAutoRotating = false;

  // Handle mouse down event
  cube.addEventListener('mousedown', (e) => {
    e.preventDefault();
    isDragging = true;
    cube.style.cursor = 'grabbing';
    previousMousePosition = {
      x: e.clientX,
      y: e.clientY
    };

    // Disable auto-rotation when user starts dragging
    if (isAutoRotating) {
      cube.classList.remove('auto-rotate');
      isAutoRotating = false;
    }
  });

  // Handle double-click event
  cube.addEventListener('dblclick', (e) => {
    e.preventDefault();
    toggleAutoRotation();
  });

  // Handle touch events for mobile
  cube.addEventListener('touchstart', (e) => {
    e.preventDefault();
    const touch = e.touches[0];
    isDragging = true;
    previousMousePosition = {
      x: touch.clientX,
      y: touch.clientY
    };

    // Check for double tap
    const currentTime = new Date().getTime();
    const tapLength = currentTime - lastTap;
    if (tapLength < 300 && tapLength > 0) {
      toggleAutoRotation();
    }
    lastTap = currentTime;
  });

  // Handle mouse move event
  document.addEventListener('mousemove', (e) => {
    if (!isDragging) return;

    const currentMousePosition = {
      x: e.clientX,
      y: e.clientY
    };

    const mouseDelta = {
      x: currentMousePosition.x - previousMousePosition.x,
      y: currentMousePosition.y - previousMousePosition.y
    };

    // Update rotation based on mouse movement
    rotationY += mouseDelta.x * 0.5;
    rotationX += mouseDelta.y * 0.5;

    // Limit vertical rotation to prevent flipping
    rotationX = Math.max(-60, Math.min(60, rotationX));

    // Apply rotation
    cube.style.transform = `rotateX(${rotationX}deg) rotateY(${rotationY}deg)`;

    previousMousePosition = {
      x: currentMousePosition.x,
      y: currentMousePosition.y
    };
  });

  // Handle touch move event
  document.addEventListener('touchmove', (e) => {
    if (!isDragging) return;

    const touch = e.touches[0];
    const currentMousePosition = {
      x: touch.clientX,
      y: touch.clientY
    };

    const mouseDelta = {
      x: currentMousePosition.x - previousMousePosition.x,
      y: currentMousePosition.y - previousMousePosition.y
    };

    // Update rotation based on touch movement
    rotationY += mouseDelta.x * 0.5;
    rotationX += mouseDelta.y * 0.5;

    // Limit vertical rotation
    rotationX = Math.max(-60, Math.min(60, rotationX));

    // Apply rotation
    cube.style.transform = `rotateX(${rotationX}deg) rotateY(${rotationY}deg)`;

    previousMousePosition = {
      x: currentMousePosition.x,
      y: currentMousePosition.y
    };
  });

  // Handle mouse up and mouse leave events
  document.addEventListener('mouseup', () => {
    isDragging = false;
    cube.style.cursor = 'grab';
  });

  document.addEventListener('mouseleave', () => {
    isDragging = false;
    cube.style.cursor = 'grab';
  });

  // Handle touch end event
  document.addEventListener('touchend', () => {
    isDragging = false;
  });

  // Toggle auto-rotation
  function toggleAutoRotation() {
    isAutoRotating = !isAutoRotating;

    if (isAutoRotating) {
      // Store current rotation before starting auto-rotation
      cube.style.transform = `rotateX(20deg) rotateY(0deg)`;
      cube.classList.add('auto-rotate');
    } else {
      cube.classList.remove('auto-rotate');
      // Restore manual rotation
      cube.style.transform = `rotateX(${rotationX}deg) rotateY(${rotationY}deg)`;
    }
  }

  // Add subtle movement on initial load
  let angle = 0;
  function subtleMovement() {
    if (!isDragging && !isAutoRotating) {
      angle += 0.2;
      const subtleX = rotationX + Math.sin(angle * 0.05) * 1;
      const subtleY = rotationY + Math.cos(angle * 0.05) * 1;
      cube.style.transform = `rotateX(${subtleX}deg) rotateY(${subtleY}deg)`;
    }
    requestAnimationFrame(subtleMovement);
  }

  // Start with auto-rotation
  toggleAutoRotation();

  // Start subtle movement loop
  subtleMovement();
}

// Initialize the premium 3D cube
initPremiumCube();

