function initParallaxTilt() {
  const card = document.querySelector('.parallax-card');
  const content = document.querySelector('.parallax-content');
  const layers = document.querySelectorAll('.parallax-layer');

  if (!card || !content || !layers.length) return;

  // Set initial transforms for layers
  layers.forEach(layer => {
    const depth = layer.getAttribute('data-depth');
    layer.style.transform = `translateZ(${depth * 100}px)`;
  });

  // Track mouse position
  let rect = card.getBoundingClientRect();
  let mouseX = 0;
  let mouseY = 0;

  // Update on window resize
  window.addEventListener('resize', () => {
    rect = card.getBoundingClientRect();
  });

  // Handle mouse movement
  card.addEventListener('mousemove', (e) => {
    mouseX = e.clientX - rect.left - rect.width / 2;
    mouseY = e.clientY - rect.top - rect.height / 2;

    const rotateY = mouseX * 0.05;
    const rotateX = -mouseY * 0.05;

    content.style.transform = `rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;

    // Move layers based on their depth
    layers.forEach(layer => {
      const depth = layer.getAttribute('data-depth');
      const translateX = mouseX * depth * 0.1;
      const translateY = mouseY * depth * 0.1;

      layer.style.transform = `translateX(${translateX}px) translateY(${translateY}px) translateZ(${depth * 100}px)`;
    });
  });

  // Reset when mouse leaves
  card.addEventListener('mouseleave', () => {
    content.style.transform = 'rotateX(0deg) rotateY(0deg)';

    layers.forEach(layer => {
      const depth = layer.getAttribute('data-depth');
      layer.style.transform = `translateZ(${depth * 100}px)`;
    });
  });
}

initParallaxTilt();

