function initHolographicCard() {
  const container = document.querySelector('.holographic-container');
  const card = document.querySelector('.holographic-card');
  const shine = document.querySelector('.holo-shine');

  if (!container || !card || !shine) return;

  let rect = card.getBoundingClientRect();
  let mouseX = 0;
  let mouseY = 0;

  // Handle window resize
  window.addEventListener('resize', () => {
    rect = card.getBoundingClientRect();
  });

  // Handle mouse movement for container
  container.addEventListener('mousemove', (e) => {
    rect = card.getBoundingClientRect();
    mouseX = e.clientX - rect.left - rect.width / 2;
    mouseY = e.clientY - rect.top - rect.height / 2;

    // Calculate rotation based on mouse position
    const rotateY = mouseX * 0.07; 
    const rotateX = -mouseY * 0.1;

    // Apply rotation to card
    card.style.transform = `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) translateZ(10px)`;

    // Apply shine effect
    const shineX = (e.clientX - rect.left) / rect.width * 100;
    const shineY = (e.clientY - rect.top) / rect.height * 100;

    shine.style.opacity = '1';
    shine.style.backgroundPosition = `${shineX}% ${shineY}%`;
    shine.style.transform = `rotate(45deg) translate(${mouseX * 0.03}px, ${mouseY * 0.03}px)`;
  });

  // Handle mouse exit
  container.addEventListener('mouseleave', () => {
    // Reset card rotation
    card.style.transform = 'perspective(1000px) rotateX(10deg) rotateY(0deg) translateZ(0)';

    // Reset shine effect
    shine.style.opacity = '0';
  });

  // Add dynamic color shifting to content elements
  const colorShift = () => {
    const time = Date.now() * 0.001;
    const content = document.querySelector('.holo-content');
    if (content) {
      const hue = ((Math.sin(time) * 0.5 + 0.5) * 360 + 230) % 360;
      content.style.filter = `hue-rotate(${hue}deg)`;
      requestAnimationFrame(colorShift);
    }
  };

  // Start color shifting animation
  colorShift();
}

// Initialize the holographic card
initHolographicCard();
