.magnetic-button-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 150px;
}

.magnetic-button {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0;
  position: relative;
  border: none;
  background: transparent;
  cursor: pointer;
  overflow: hidden;
  outline: none;
  transform: translate3d(0, 0, 0);
  transition: all 0.3s cubic-bezier(0.2, 1, 0.8, 1);
}

.button-text {
  position: relative;
  display: inline-block;
  font-size: 16px;
  font-weight: 600;
  color: #333;
  padding: 14px 24px;
  transition: all 0.3s cubic-bezier(0.2, 1, 0.8, 1);
  transform: translateZ(0);
}

.button-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 48px;
  height: 48px;
  background: #4285f4;
  color: white;
  border-radius: 50%;
  transform: translateZ(0);
  transition: all 0.3s cubic-bezier(0.2, 1, 0.8, 1);
}

.magnetic-button::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(66, 133, 244, 0.08);
  border-radius: 30px;
  transition: all 0.3s cubic-bezier(0.2, 1, 0.8, 1);
  transform: translateZ(0);
  z-index: -1;
}

.magnetic-button:hover::before {
  opacity: 1;
  transform: scale(1.02, 1.1);
}

.magnetic-button:hover .button-text {
  transform: translateX(-5px);
  color: #2b67ca;
}

.magnetic-button:hover .button-icon {
  transform: translateX(5px) scale(1.1);
  box-shadow: 0 5px 15px rgba(66, 133, 244, 0.4);
}

.magnetic-button:active {
  transform: scale(0.98);
}

