const button = this.querySelector('.magnetic-button');
const magneticArea = this.querySelector('.magnetic-button-container');
const buttonText = this.querySelector('.button-text');
const buttonIcon = this.querySelector('.button-icon');

if (button && magneticArea) {
  const strength = 40; // Magnetic pull strength

  magneticArea.addEventListener('mousemove', (e) => {
    const rect = magneticArea.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    // Calculate distance from center in percentage
    const deltaX = Math.floor((e.clientX - centerX) / 8);
    const deltaY = Math.floor((e.clientY - centerY) / 8);

    // Move the button subtly to follow cursor
    button.style.transform = `translate3d(${deltaX}px, ${deltaY}px, 0)`;

    // Calculate direction for text and icon movement
    const direction = Math.abs(deltaX) > Math.abs(deltaY) 
      ? (deltaX > 0 ? 1 : -1) 
      : 0;

    // Add subtle movement to text and icon
    if (buttonText && buttonIcon) {
      buttonText.style.transform = `translateX(${direction * -2}px)`;
      buttonIcon.style.transform = `translateX(${direction * 2}px) scale(${direction > 0 ? 1.1 : 1})`;
    }
  });

  // Reset position when mouse leaves
  magneticArea.addEventListener('mouseleave', () => {
    button.style.transform = 'translate3d(0, 0, 0)';

    if (buttonText && buttonIcon) {
      buttonText.style.transform = 'translateX(0)';
      buttonIcon.style.transform = 'translateX(0) scale(1)';
    }
  });

  // Add subtle push effect when clicked
  button.addEventListener('mousedown', () => {
    button.style.transform = 'translate3d(0, 0, 0) scale(0.97)';
  });

  button.addEventListener('mouseup', () => {
    button.style.transform = 'translate3d(0, 0, 0)';
  });
}

