function initMagneticEffect() {
  const container = document.querySelector('.magnetic-container');
  const button = document.querySelector('.magnetic-button');

  if (!container || !button) return;

  // Set the strength of the magnetic effect
  const magneticStrength = 0.3;
  const maxMove = 15; // Maximum pixel movement

  container.addEventListener('mousemove', (e) => {
    const rect = container.getBoundingClientRect();
    const btnRect = button.getBoundingClientRect();

    // Calculate the center point of the button
    const btnCenterX = btnRect.left + btnRect.width / 2;
    const btnCenterY = btnRect.top + btnRect.height / 2;

    // Calculate the distance between cursor and button center
    const distanceX = e.clientX - btnCenterX;
    const distanceY = e.clientY - btnCenterY;

    // Calculate the distance from cursor to button center
    const distance = Math.sqrt(distanceX * distanceX + distanceY * distanceY);

    // Only apply the effect if the cursor is within range
    if (distance < rect.width / 1.5) {
      // Calculate movement based on distance and strength
      const moveX = (distanceX / rect.width) * maxMove * magneticStrength;
      const moveY = (distanceY / rect.height) * maxMove * magneticStrength;

      // Apply the transform
      button.style.transform = `translate(${moveX}px, ${moveY}px)`;
    }
  });

  // Reset position when mouse leaves
  container.addEventListener('mouseleave', () => {
    button.style.transform = '';
  });
}

initMagneticEffect();

