  width: 100%;
  height: 100%;
  object-fit: cover;
  transform: scale(1.2);
  transition: transform 0.5s ease;
}

.image-reveal-item:hover .reveal-image {
  transform: scale(1);
}

.split-text {
  position: relative;
  display: inline-block;
  font-size: 3rem;
  font-weight: 800;
  color: #333;
  cursor: pointer;
  transition: color 0.3s ease;
  text-align: center;
}

.split-text::before,
.split-text::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  color: #333;
  clip-path: rect(0, 0, 100%, 0);
  transition: clip-path 0.4s ease, transform 0.4s ease;
  pointer-events: none;
}

.split-text::before {
  clip-path: rect(0, 50%, 100%, 0);
  color: #7c4dff;
}

.split-text::after {
  clip-path: rect(0, 100%, 100%, 50%);
  color: #4dcaff;
}

.split-text:hover {
  color: transparent;
}

.split-text:hover::before {
  clip-path: rect(0, 50%, 100%, 0);
  transform: translate(-10px, -5px);
}

.split-text:hover::after {
  clip-path: rect(0, 100%, 100%, 50%);
  transform: translate(10px, 5px);
}

