function initLiquidButtonEffect() {
  const liquidButton = document.querySelector('.liquid-button');

  if (!liquidButton) return;

  // Add ripple effect on click
  liquidButton.addEventListener('click', function(e) {
    // Remove any previous ripple
    liquidButton.classList.remove('animate');

    // Calculate ripple position
    const rect = liquidButton.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Set the ripple position
    const ripple = document.createElement('div');
    ripple.className = 'ripple';
    ripple.style.left = x + 'px';
    ripple.style.top = y + 'px';
    liquidButton.appendChild(ripple);

    // Animate the ripple
    liquidButton.style.setProperty('--ripple-x', x + 'px');
    liquidButton.style.setProperty('--ripple-y', y + 'px');
    liquidButton.classList.add('animate');

    // Clean up the ripple element after animation
    setTimeout(() => {
      ripple.remove();
    }, 1000);
  });

  // Add bubble animations on hover
  liquidButton.addEventListener('mouseenter', function() {
    createBubbles(liquidButton);
  });
}

function createBubbles(button) {
  const bubbleCount = 10;
  const buttonRect = button.getBoundingClientRect();

  for (let i = 0; i < bubbleCount; i++) {
    const bubble = document.createElement('div');
    bubble.className = 'liquid-bubble';

    // Apply styles
    bubble.style.position = 'absolute';
    bubble.style.backgroundColor = 'rgba(255, 255, 255, 0.15)';
    bubble.style.borderRadius = '50%';
    bubble.style.pointerEvents = 'none';

    // Random size and position
    const size = 5 + Math.random() * 15;
    const left = Math.random() * 100;
    const animationDuration = 1 + Math.random() * 2;

    bubble.style.width = `${size}px`;
    bubble.style.height = `${size}px`;
    bubble.style.left = `${left}%`;
    bubble.style.bottom = '-20px';
    bubble.style.opacity = '0';
    bubble.style.transform = 'translateY(0)';
    bubble.style.transition = `all ${animationDuration}s ease-out`;

    button.appendChild(bubble);

    // Trigger animation after a small delay (for staggered effect)
    setTimeout(() => {
      bubble.style.transform = 'translateY(-60px)';
      bubble.style.opacity = Math.random() * 0.5;
    }, i * 150);

    // Remove bubble after animation
    setTimeout(() => {
      bubble.remove();
    }, animationDuration * 1000 + i * 150);
  }
}

// Initialize the liquid button effect
initLiquidButtonEffect();
