css:
.corp-highlight-container {
  font-family: 'Inter', sans-serif;
  padding: 2.5rem 2rem;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(to right, #f8f9fa, #f5f7fa);
  border-radius: 8px;
}

.corp-highlight-heading {
  font-size: 1.75rem;
  font-weight: 600;
  line-height: 1.4;
  color: #2c3e50;
  text-align: center;
  max-width: 600px;
  margin: 0;
  letter-spacing: -0.01em;
}

.corp-highlight {
  position: relative;
  display: inline-block;
  color: #2563eb;
  font-weight: 700;
  z-index: 1;
  transition: color 0.3s ease;
}

.corp-highlight::before {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 30%;
  background-color: rgba(37, 99, 235, 0.12);
  z-index: -1;
  transform: scaleX(0.3) translateY(0);
  opacity: 0;
  transform-origin: left;
  transition: transform 0.6s cubic-bezier(0.19, 1, 0.22, 1), 
              opacity 0.6s ease, 
              height 0.6s ease;
}

.corp-highlight-container:hover .corp-highlight::before {
  transform: scaleX(1) translateY(0);
  height: 30%;
  opacity: 1;
}

.corp-highlight::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 2px;
  background-color: #2563eb;
  transform: scaleX(0);
  transform-origin: left;
  transition: transform 0.4s ease;
}

.corp-highlight-container:hover .corp-highlight::after {
  transform: scaleX(1);
  transition-delay: 0.2s;
}

