// Add animation to the professional text highlight
const container = this.querySelector('.corp-highlight-container');
const highlight = this.querySelector('.corp-highlight');

if (container && highlight) {
  // Initial state animation setup
  highlight.style.opacity = '0.9';
  highlight.style.transform = 'translateY(2px)';
  highlight.style.transition = 'opacity 0.5s ease, transform 0.5s ease, color 0.3s ease';

  // Create a subtle glow effect
  const glow = document.createElement('div');
  glow.className = 'highlight-glow';
  glow.style.cssText = `
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 0;
    background: radial-gradient(ellipse at center, rgba(37, 99, 235, 0.2) 0%, rgba(37, 99, 235, 0) 70%);
    z-index: -2;
    opacity: 0;
    transform: translateY(5px);
    transition: opacity 0.6s ease, transform 0.6s ease, height 0.6s ease;
  `;

  highlight.appendChild(glow);

  // Animate in with slight delay
  setTimeout(() => {
    highlight.style.opacity = '1';
    highlight.style.transform = 'translateY(0)';
  }, 300);

  // Add hover interaction
  container.addEventListener('mouseenter', () => {
    glow.style.opacity = '1';
    glow.style.height = '100%';
    glow.style.transform = 'translateY(0px)';
  });

  container.addEventListener('mouseleave', () => {
    glow.style.opacity = '0';
    glow.style.height = '0';
    glow.style.transform = 'translateY(5px)';
  });

  // Add subtle animation when the highlight is clicked
  highlight.addEventListener('click', () => {
    highlight.style.color = '#1d4ed8';

    const before = window.getComputedStyle(
      highlight, '::before'
    );

    // Animation effect - create a pulse
    highlight.style.transform = 'scale(1.05)';
    setTimeout(() => {
      highlight.style.transform = 'scale(1)';
    }, 200);

    // Reset color after delay
    setTimeout(() => {
      highlight.style.color = '#2563eb';
    }, 500);
  });
}

