// Enhance the typing animation with dynamic content if needed
const container = this.querySelector('.corporate-typing-container');
const textElements = this.querySelectorAll('.typing-item');

if (container && textElements.length) {
  // Add a subtle entrance animation
  container.style.opacity = '0';
  container.style.transform = 'translateY(20px)';
  container.style.transition = 'opacity 0.8s ease, transform 0.8s ease';

  setTimeout(() => {
    container.style.opacity = '1';
    container.style.transform = 'translateY(0)';
  }, 300);

  // Calculate optimal width for text container
  const textContainer = this.querySelector('.corporate-typing-text');
  if (textContainer) {
    let maxWidth = 0;

    // Find the widest text element
    textElements.forEach(el => {
      const tempSpan = document.createElement('span');
      tempSpan.style.visibility = 'hidden';
      tempSpan.style.position = 'absolute';
      tempSpan.style.fontSize = '1.6rem';
      tempSpan.style.fontWeight = '700';
      tempSpan.textContent = el.textContent;
      document.body.appendChild(tempSpan);

      const width = tempSpan.offsetWidth;
      if (width > maxWidth) {
        maxWidth = width;
      }

      document.body.removeChild(tempSpan);
    });

    // Set minimum width to fit the longest text
    if (maxWidth > 0) {
      textContainer.style.minWidth = `${maxWidth + 20}px`;
    }
  }
}

