// Text Scrambler
class TextScrambler {
  constructor(el) {
    this.el = el;
    this.chars = '!<>-_\\|/[]{}â€”=+*^?#_____';
    this.update = this.update.bind(this);
  }

  setText(newText) {
    const oldText = this.el.innerText;
    const length = Math.max(oldText.length, newText.length);
    const promise = new Promise(resolve => this.resolve = resolve);
    this.queue = [];

    for (let i = 0; i < length; i++) {
      const from = oldText[i] || '';
      const to = newText[i] || '';
      const start = Math.floor(Math.random() * 40);
      const end = start + Math.floor(Math.random() * 40);
      this.queue.push({ from, to, start, end });
    }

    cancelAnimationFrame(this.frameRequest);
    this.frame = 0;
    this.update();
    return promise;
  }

  update() {
    let output = '';
    let complete = 0;

    for (let i = 0, n = this.queue.length; i < n; i++) {
      let { from, to, start, end, char } = this.queue[i];

      if (this.frame >= end) {
        complete++;
        output += to;
      } else if (this.frame >= start) {
        if (!char || Math.random() < 0.28) {
          char = this.randomChar();
          this.queue[i].char = char;
        }
        output += char;
      } else {
        output += from;
      }
    }

    this.el.innerText = output;

    if (complete === this.queue.length) {
      this.resolve();
    } else {
      this.frameRequest = requestAnimationFrame(this.update);
      this.frame++;
    }
  }

  randomChar() {
    return this.chars[Math.floor(Math.random() * this.chars.length)];
  }
}

function initScrambler() {
  const el = document.querySelector('.scramble-text');
  if (!el) return;

  const fx = new TextScrambler(el);

  const scrambleText = () => {
    fx.setText('AnimX Text Scrambler').then(() => {
      setTimeout(scrambleText, 4000);
    });
  };

  scrambleText();

  // Add replay button functionality
  const replayBtn = document.getElementById('replay-scramble');
  if (replayBtn) {
    replayBtn.addEventListener('click', () => {
      fx.setText('AnimX Text Scrambler');
    });
  }
}

initScrambler();

css:
.gradient-text {
  font-size: 3.5rem;
  font-weight: 900;
  text-align: center;
  background-image: linear-gradient(90deg, #7c4dff, #ff4d7c, #4dcaff, #4dffcb, #7c4dff);
  background-size: 400% 100%;
  background-clip: text;
  -webkit-background-clip: text;
  color: transparent;
  animation: gradient-shift 8s linear infinite;
}

@keyframes gradient-shift {
  0% {
    background-position: 0% 50%;
  }
  100% {
    background-position: 400% 50%;
  }
}

.text-3d-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 200px;
  perspective: 1000px;
}

.text-3d {
  position: relative;
  width: 200px;
  height: 200px;
  transform-style: preserve-3d;
  animation: rotate3d 10s infinite linear;
}

.face {
  position: absolute;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2.5rem;
  font-weight: 800;
  backface-visibility: hidden;
  color: white;
  text-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
}

.front {
  transform: translateZ(100px);
  background-color: rgba(124, 77, 255, 0.8);
}

.back {
  transform: rotateY(180deg) translateZ(100px);
  background-color: rgba(255, 77, 124, 0.8);
}

.right {
  transform: rotateY(90deg) translateZ(100px);
  background-color: rgba(77, 202, 255, 0.8);
}

.left {
  transform: rotateY(-90deg) translateZ(100px);
  background-color: rgba(255, 202, 77, 0.8);
}

.top {
  transform: rotateX(90deg) translateZ(100px);
  background-color: rgba(77, 255, 107, 0.8);
}

.bottom {
  transform: rotateX(-90deg) translateZ(100px);
  background-color: rgba(255, 77, 249, 0.8);
}

@keyframes rotate3d {
  0% {
    transform: rotateX(0deg) rotateY(0deg);
  }
  100% {
    transform: rotateX(360deg) rotateY(360deg);
  }
}

.glitch-text {
  position: relative;
  font-size: 4rem;
  font-weight: 800;
  color: white;
  text-shadow: 0.05em 0 0 rgba(255, 0, 0, 0.75),
              -0.025em -0.05em 0 rgba(0, 255, 0, 0.75),
              0.025em 0.05em 0 rgba(0, 0, 255, 0.75);
  animation: glitch 2s infinite;
}

.glitch-text::before,
.glitch-text::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0.8;
}

.glitch-text::before {
  animation: glitch-effect 2s infinite;
  clip-path: polygon(0 0, 100% 0, 100% 35%, 0 35%);
  transform: translate(-0.04em, -0.025em);
  opacity: 0.75;
  color: rgb(249, 16, 145);
}

.glitch-text::after {
  animation: glitch-effect 2.5s infinite reverse;
  clip-path: polygon(0 65%, 100% 65%, 100% 100%, 0 100%);
  transform: translate(0.04em, 0.025em);
  opacity: 0.75;
  color: rgb(27, 194, 246);
}

@keyframes glitch-effect {
  0% {
    transform: translate(0);
  }
  10% {
    transform: translate(-5px, 5px);
  }
  20% {
    transform: translate(-5px, -5px);
  }
  30% {
    transform: translate(5px, 5px);
  }
  40% {
    transform: translate(5px, -5px);
  }
  50% {
    transform: translate(-5px, 5px);
  }
  60% {
    transform: translate(5px, 5px);
  }
  70% {
    transform: translate(-5px, -5px);
  }
  80% {
    transform: translate(-5px, 5px);
  }
  90% {
    transform: translate(5px, -5px);
  }
  100% {
    transform: translate(0);
  }
}

@keyframes glitch {
  1% {
    transform: rotateX(10deg) skewX(90deg);
  }
  2% {
    transform: rotateX(0deg) skewX(0deg);
  }
  40% {
    transform: rotateX(0deg) skewX(0deg);
  }
  41% {
    transform: rotateX(10deg) skewX(-90deg);
  }
  42% {
    transform: rotateX(0deg) skewX(0deg);
  }
  100% {
    transform: rotateX(0deg) skewX(0deg);
  }
}

.perspective-container {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px;
  background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
  border-radius: 12px;
  perspective: 1000px;
  overflow: hidden;
}

.perspective-wrapper {
  transform-style: preserve-3d;
  perspective: 1000px;
  position: relative;
  height: 60px;
  overflow: hidden;
}

.perspective-text {
  position: relative;
  animation: rotateText 8s ease infinite;
  transform-style: preserve-3d;
}

.perspective-text p {
  margin: 0;
  height: 60px;
  line-height: 60px;
  font-size: 3rem;
  font-weight: 800;
  color: transparent;
  background: linear-gradient(45deg, #7c4dff, #4dcaff);
  -webkit-background-clip: text;
  background-clip: text;
  text-align: center;
  text-transform: uppercase;
  text-shadow: 0 5px 15px rgba(124, 77, 255, 0.3);
  position: relative;
  transform-origin: center center -30px;
  transform-style: preserve-3d;
  transform: rotateX(-90deg) translateY(-30px);
  animation: rollIn 1.5s ease-in-out infinite;
  animation-play-state: paused;
  letter-spacing: 2px;
}

/* Apply animation to each paragraph with delay */
.perspective-text p:nth-child(1) {
  animation-delay: 0s;
  animation-play-state: running;
}

.perspective-text p:nth-child(2) {
  animation-delay: 1.5s;
  animation-play-state: running;
}

.perspective-text p:nth-child(3) {
  animation-delay: 3s;
  animation-play-state: running;
}

.perspective-text p:nth-child(4) {
  animation-delay: 4.5s;
  animation-play-state: running;
}

.perspective-text p:nth-child(5) {
  animation-delay: 6s;
  animation-play-state: running;
}

@keyframes rotateText {
  0%, 15% { transform: translateZ(0) rotateX(0); }
  20%, 35% { transform: translateZ(0) rotateX(-90deg); }
  40%, 55% { transform: translateZ(0) rotateX(-180deg); }
  60%, 75% { transform: translateZ(0) rotateX(-270deg); }
  80%, 95% { transform: translateZ(0) rotateX(-360deg); }
  100% { transform: translateZ(0) rotateX(-360deg); }
}

@keyframes rollIn {
  0% {
    transform: rotateX(-90deg) translateY(-30px);
    opacity: 0;
  }
  20% {
    transform: rotateX(0deg) translateY(0);
    opacity: 1;
  }
  70% {
    transform: rotateX(0deg) translateY(0);
    opacity: 1;
  }
  100% {
    transform: rotateX(90deg) translateY(30px);
    opacity: 0;
  }
}

/* Add light reflection */
.perspective-text p::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, 
    transparent 0%, 
    rgba(255, 255, 255, 0.1) 45%, 
    rgba(255, 255, 255, 0.4) 50%, 
    rgba(255, 255, 255, 0.1) 55%, 
    transparent 100%);
  transform: translateX(-100%);
  animation: lightSweep 3s ease-in-out infinite;
  animation-play-state: paused;
}

.perspective-text p:nth-child(1)::after { animation-delay: 0.5s; animation-play-state: running; }
.perspective-text p:nth-child(2)::after { animation-delay: 2s; animation-play-state: running; }
.perspective-text p:nth-child(3)::after { animation-delay: 3.5s; animation-play-state: running; }
.perspective-text p:nth-child(4)::after { animation-delay: 5s; animation-play-state: running; }
.perspective-text p:nth-child(5)::after { animation-delay: 6.5s; animation-play-state: running; }

@keyframes lightSweep {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

