function initPerspectiveText() {
  const container = document.querySelector('.perspective-container');

  if (!container) return;

  // Add depth and tilt effect on mouse move
  container.addEventListener('mousemove', (e) => {
    const wrapper = document.querySelector('.perspective-wrapper');
    if (!wrapper) return;

    const rect = container.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    // Calculate mouse position relative to center
    const mouseX = e.clientX - centerX;
    const mouseY = e.clientY - centerY;

    // Calculate rotation angle
    const rotateX = mouseY * -0.03;
    const rotateY = mouseX * 0.03;

    // Apply transformation
    wrapper.style.transform = `rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;

    // Add depth effect to text
    const textElements = document.querySelectorAll('.perspective-text p');
    textElements.forEach((text, index) => {
      const z = (index + 1) * 5;
      const scale = 1 + (index * 0.05);
      text.style.transform = `rotateX(-90deg) translateY(-30px) translateZ(${z}px) scale(${scale})`;
    });
  });

  // Reset on mouse leave
  container.addEventListener('mouseleave', () => {
    const wrapper = document.querySelector('.perspective-wrapper');
    if (!wrapper) return;

    wrapper.style.transform = '';

    const textElements = document.querySelectorAll('.perspective-text p');
    textElements.forEach(text => {
      text.style.transform = '';
    });
  });
}

// Initialize perspective text
initPerspectiveText();
